mapper.update:
update user
set age = #{age,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  name = #{name,jdbcType=VARCHAR},
  real_name = #{realName,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, age, create_time, name, real_name
from user
where id = #{id,jdbcType=VARCHAR}
